(8447, 'Where is Ezion Geber?', 'Near Elath on the shore of the Red Sea, in the land of Edom', 1, '2021-08-22 16:14:36', '2021-08-22 16:14:36', 'bible-qna', NULL, NULL, 0, 3, 3, 1580, NULL, NULL, 1),
(8448, 'What did King Solomon build at Ezion Geber?', 'A fleet of ships', 1, '2021-08-22 16:14:36', '2021-08-22 16:14:36', 'bible-qna', NULL, NULL, 0, 3, 3, 1580, NULL, NULL, 1),
(8449, 'Who built a fleet of ships?', 'King Solomon', 1, '2021-08-22 16:14:36', '2021-08-22 16:14:36', 'bible-qna', NULL, NULL, 0, 3, 3, 1580, NULL, NULL, 1),
